mapper.select:
select count(*) as count from tb_board where bo_del_yn = 'N' and bo_type = #{bo_type}
AND bo_title LIKE concat('%',#{searchWord},'%')
AND bo_content LIKE concat('%',#{searchWord},'%')
AND bo_title LIKE concat('%',#{searchWord},'%') or bo_content LIKE concat('%', #{searchWord} , '%')
AND bo_writer_name = #{searchWord}

mapper.update:
UPDATE tb_board SET
	 bo_title = #{bo_title}
	,bo_content = #{bo_content}
	,bo_open_yn = #{bo_open_yn}
	,upd_date = now()
	,upd_user = #{upd_user}
WHERE 
	bo_seq_no = #{bo_seq_no}

UPDATE tb_board SET
	bo_del_yn = 'Y'
	,upd_date = now()
	,upd_user = #{upd_user}
WHERE 
	bo_seq_no = #{bo_seq_no}
